import{_ as a,c as e,o as t,a as n}from"./app-B5qeYJMk.js";const i="/zh/projects/%E5%9B%BE1%E8%BF%90%E5%8A%A8%E7%9B%AE%E6%A0%87%E6%8E%A7%E5%88%B6%E4%B8%8E%E8%87%AA%E5%8A%A8%E8%BF%BD%E8%B8%AA%E7%B3%BB%E7%BB%9F%E7%BB%93%E6%9E%84%E7%A4%BA%E6%84%8F%E5%8F%8A%E6%91%86%E6%94%BE%E4%BD%8D%E7%BD%AE%E5%9B%BE.png",l={},c=n('<h2 id="项目任务和要求" tabindex="-1"><a class="header-anchor" href="#项目任务和要求"><span>项目任务和要求</span></a></h2><h3 id="任务" tabindex="-1"><a class="header-anchor" href="#任务"><span>任务</span></a></h3><p> 设计制作一个运动目标控制与自动追踪系统。系统包括模拟目标运动的红色光斑位置控制系统和指示自动追踪的绿色光斑位置控制系统。系统结构示意及摆 放位置见图1（a）。图中两个激光笔固定在各自独立的二维电控云台上。</p><div align="center"><img src="'+i+'" align="middle"><br> 图1 运动目标控制与自动追踪系统结构示意及摆放位置图 </div><p> 红色激光笔发射的光斑用来模拟运动目标，光斑落在正前方距离1m处的白色屏幕上，光斑直径≤1cm。红色光斑位置控制系统控制光斑能在屏幕范围内任意移动。</p><p> 绿色激光笔发射的光斑由绿色光斑位置系统控制，用于自动追踪屏幕上的红色光斑，指示目标的自动追踪效果，光斑直径≤1cm。绿色激光笔放置线段如图1（b）所示，该线段与屏幕平行，位于红色激光笔两侧，距红色激光笔距离大于0.4m、小于1m。绿色激光笔在两个放置线段上任意放置。</p><p> 屏幕为白色，有效面积大于 0.6╳0.6m2。用铅笔在屏幕中心画出一个边长0.5m的正方形，标识屏幕的边线；所画的正方形的中心为原点，用铅笔画出原点位置，所用铅笔痕迹宽≤1mm。红色、绿色光斑位置控制系统必须相互独立，之间不得有任何方式通信；光斑直径小于1cm；屏幕上无任何电子元件；控制系统不能采用台式计算机或笔记本电脑。</p><h3 id="要求" tabindex="-1"><a class="header-anchor" href="#要求"><span>要求</span></a></h3><h4 id="基本要求" tabindex="-1"><a class="header-anchor" href="#基本要求"><span>基本要求</span></a></h4><ol><li><p>设置运动目标位置复位功能。执行此功能，红色光斑能从屏幕任意位置回到原点。光斑中心距原点误差≤2cm。</p></li><li><p>启动运动目标控制系统。红色光斑能在30秒内沿屏幕四周边线顺时针移动一周，移动时光斑中心距边线距离≤2cm。</p></li><li><p>用约1.8cm宽的黑色电工胶带沿A4纸四边贴一个长方形，构成A4靶纸。将此A4靶纸贴在屏幕自定的位置。启动运动目标控制系统，红色光斑能在30 秒内沿胶带顺时针移动一周。超时不得分，光斑完全脱离胶带一次扣 2 分，连续脱离胶带移动5cm以上记为0分。</p></li><li><p>将上述A4靶纸以任意旋转角度贴在屏幕任意位置。启动运动目标控制系统，要求同 3 。</p></li></ol><h4 id="发挥部分" tabindex="-1"><a class="header-anchor" href="#发挥部分"><span>发挥部分</span></a></h4><ol><li><p>运动目标位置复位，一键启动自动追踪系统，控制绿色光斑能在2 秒内追踪红色光斑，追踪成功发出连续声光提示。此时两个光斑中心距离应≤3cm。</p></li><li><p>运动目标重复基本要求 3 ~ 4 的动作。绿色激光笔发射端可以放置在其放置线段的任意位置，同时启动运动目标及自动追踪系统，绿色光斑能自动追踪红色光斑。启动系统2秒后，应追踪成功，发出连续声光提示。此后，追踪过程中两个光斑中心距离大于 3cm 时，定义为追踪失败，一次扣 2 分。连续追踪失败3秒以上记为0分。</p></li></ol><p> 运动目标控制系统和自动追踪系统均需设置暂停键。同时按下暂停键，红色和绿色光斑应立即制动，以便测量两个光斑中心距离。</p><h2 id="实现方案" tabindex="-1"><a class="header-anchor" href="#实现方案"><span>实现方案</span></a></h2>',14),r=[c];function s(o,p){return t(),e("div",null,r)}const m=a(l,[["render",s],["__file","Moving target control and automatic tracking system.html.vue"]]),d=JSON.parse('{"path":"/zh/my-projects/Moving%20target%20control%20and%20automatic%20tracking%20system.html","title":"运动目标控制与自动追踪系统","lang":"zh-CN","frontmatter":{"title":"运动目标控制与自动追踪系统"},"headers":[{"level":2,"title":"项目任务和要求","slug":"项目任务和要求","link":"#项目任务和要求","children":[{"level":3,"title":"任务","slug":"任务","link":"#任务","children":[]},{"level":3,"title":"要求","slug":"要求","link":"#要求","children":[]}]},{"level":2,"title":"实现方案","slug":"实现方案","link":"#实现方案","children":[]}],"git":{"updatedTime":1720522911000,"contributors":[{"name":"uint16kun","email":"uint16kun","commits":1}]},"filePathRelative":"zh/my-projects/Moving target control and automatic tracking system.md","excerpt":"<h2>项目任务和要求</h2>\\n<h3>任务</h3>\\n<p> 设计制作一个运动目标控制与自动追踪系统。系统包括模拟目标运动的红色光斑位置控制系统和指示自动追踪的绿色光斑位置控制系统。系统结构示意及摆\\n放位置见图1（a）。图中两个激光笔固定在各自独立的二维电控云台上。</p>\\n<div align=\\"center\\">    \\n    <img src=\\"/zh/projects/图1运动目标控制与自动追踪系统结构示意及摆放位置图.png\\" align=\\"middle\\">\\n    <br><br>\\n    图1 运动目标控制与自动追踪系统结构示意及摆放位置图\\n</div>\\n<p> 红色激光笔发射的光斑用来模拟运动目标，光斑落在正前方距离1m处的白色屏幕上，光斑直径≤1cm。红色光斑位置控制系统控制光斑能在屏幕范围内任意移动。</p>"}');export{m as comp,d as data};
